import React, { Component } from 'react';
import Person from './Person/Person';
import './App.css';

class App extends Component {
  state = {
    persons: [
      { id: '1', name: 'Joana', age: 22 },
      { id: '2', name: 'Joan', age: 18 },
      { id: '3', name: 'Jo', age: 15 },
    ],
    showPersons: true
  }

  // Two way binding example
  handleNameChangeByClick = (newName) => {
    console.log("handleNameChangeByClick executed");
    this.setState({
      persons: [{ id: '1', name: newName, age: 22 },
      { id: '2', name: newName, age: 18 },
      { id: '3', name: newName, age: 15 }]
    })
  }

  togglePersonsHandler = (event, id) => {
    console.log("togglePersonsHandler executed");
    this.setState({
      persons: [{ id: '1', name: event.target.value, age: 22 },
      { id: '2', name: event.target.value, age: 18 },
      { id: '3', name: event.target.value, age: 15 }]
    })
  }

  render() {
    const style = {
      backgroundColor: "white",
      font: "inherit",
      border: "1px solid blue",
      padings: "8px",
      cursor: "pointer"
    };
    return (
      <div className="App">
        <h1>Hello, world!</h1>
        <button
          style={style}
          onClick={() => this.togglePersonsHandler("the new name passed with errow function")}>Toggle persons
          </button>
        {this.showPersons === true ?
          <div>
            <Person name="liron" age="31"> more information</Person>
            <Person
              name={this.state.persons[0].name}
              age={this.state.persons[0].age}
              click={this.togglePersonsHandler}
              change={this.handleNameChange.bind(this)}
            > more information
            </Person>
          </div> : null}
      </div>
    );
  }
}

export default App;
