import React, { Component } from 'react';
import Person from './Person/Person';
import './App.css';

class App extends Component {
  state = {
    persons: [
      { id: '1', name: 'Joana', age: 22 },
      { id: '2', name: 'Joan', age: 18 },
      { id: '3', name: 'Jo', age: 15 },
    ],
    showPersons: true
  }

  // Two way binding example
  handleNameChange = (newName, event, id) => {
    console.log("handleNameChange executed");
    this.setState({
      persons: [{ id: '1', name: newName, age: 22 },
      { id: '2', name: newName, age: 18 },
      { id: '3', name: newName, age: 15 }]
    })
  }

  render() {
    return (
      <div className="App">
        <h1>Hello, world!</h1>
        <Person name="liron" age="31"> more information</Person>
        <Person
          name={this.state.persons[0].name}
          age={this.state.persons[0].age}
          click={this.handleNameChange}> more information</Person>
        <button onClick={this.handleNameChange}>Toggle persons</button>
      </div>
    );
  }
}

export default App;
